  #{flowmeter,jdbcType=BIT}, #{temperature,jdbcType=DOUBLE}, #{battery,jdbcType=INTEGER}, 
  #{contain1,jdbcType=VARCHAR}, #{contain2,jdbcType=VARCHAR}, #{contain3,jdbcType=VARCHAR}
  )

